// Api key - 553ed82e024e4a7c9055c838869bd008

import React, { useState } from "react";
import MealList from "./MealList";
//import Login from "./Login";
import { useUser } from './userContext';

function MealApp() {
  const [mealData, setMealData] = useState(null);
  const [calories, setCalories] = useState(2000);
  const { username } = useUser();

  function getMealData() {
    fetch(
      `https://api.spoonacular.com/mealplanner/generate?apiKey=553ed82e024e4a7c9055c838869bd008&timeFrame=day&targetCalories=${calories}`
    )
      .then((response) => response.json())
      .then((data) => {
        setMealData(data);
      })
      .catch(() => {
        console.log("error");
      });
  }

  function handleChange(e) {
    setCalories(e.target.value);
  }

  return (
    <div className="App">
      <section className="header">
        <h1>Healthy & Happy Eating Awaits {username}!!ðŸ˜Š</h1>
      </section>
      <section className="controls">
        <input
          type="number"
          placeholder="Calories (e.g. 2000)"
          onChange={handleChange}
        />
        <button className='meal-btn' onClick={getMealData}>Get Daily Meal Plan</button>
      </section>
      {mealData && <MealList mealData={mealData} />}
    </div>
  );
}

export default MealApp;
