import React, { useState } from "react";
import email_icon from "./email.png"
import password_icon from "./pass.png"
import person from "./person.png"
import "./Auth.css";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebase";



function Auth(){
  const [action,setAction] = useState("Sign Up");
  const [name,setName] = useState('')
  const [email,setEmail]= useState('')
  const [password,setPassword] = useState('')

  const navigate = useNavigate()

  const handleSubmit = async(e) => {
    e.preventDefault();

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password)
      console.log(userCredential)
      const user = userCredential.user;
      localStorage.setItem('token', user.accessToken)
      localStorage.setItem('user',JSON.stringify(user))
      navigate('/')

    } catch (error) {
      console.error(error);
    }
  }


  return (
    <div className="container">
    <div className="header">
      <div className="text">{action}</div>
      <div className="underline"></div>
    </div>
    <div className="inputs">
      {action==="Login"?<div></div>:  <div className="input">
        <img src={person}/>
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)}/>
      </div> }
      <div className="input">
        <img  src={email_icon}/>
        <input type="email" placeholder="Email Id" value={email} onChange={(e) => setEmail(e.target.value)}/>
      </div>
      <div className="input">
        <img  src={password_icon}/>
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)}/>
      </div>
    </div>
    <div className="submit-container">
      <div className={action==="Login"?"submit gray":"submit"} onClick={() => {setAction("Sign Up")}} onSubmit={handleSubmit}>Sign up</div>
      <div className={action==="Sign Up"?"submit gray":"submit"} onClick={() => {setAction("Login")}} onSubmit={handleSubmit}>Login</div>
    </div>
    </div>
  )
}

export default Auth;
